data.action:
function eval_slowanim(xml) {
        var xoffset = xml.get("offsetX"); var yoffset = xml.get("offsetY");
        var animstr = '<animation id="' +xml.get("id")+ '">';

        for (framex in 0 ... xml.get("count")) {
            str += '<frame x="' +(xml.get("x") + framex)+ '" length="' +xml.get("mult")+ '" ';
            if (xoffset != null) {str += 'offsetX="' +xoffset+ '"';}
            if (yoffset != null) {str += 'offsetY="' +yoffset+'"';}
            str += "/>";
        }
        str += '</animation>';
        evalXml(str);
        return str;
    }
